export function HeaderContent() {
  const [isScrolled, setIsScrolled] = useState(false);
  const pathname = usePathname();
  const t = useTranslations('Header');
  const scrollRef = useRef<boolean>(false);

  // Check if we're on the homepage, including locale-prefixed routes
  // This handles paths like /, /pt, /es, /br, etc. as homepage equivalents
  const isRootPath = pathname === '/' || /^\/[a-z]{2}(-[a-z]{2})?$/.test(pathname);

  useEffect(() => {
    const handleScroll = () => {
      const shouldBeScrolled = window.scrollY > 0;
      if (scrollRef.current !== shouldBeScrolled) {
        scrollRef.current = shouldBeScrolled;
        setIsScrolled(shouldBeScrolled);
      }
    };

    // Check initial scroll position
    handleScroll();

    // Add scroll event listener with passive option for better performance
    window.addEventListener('scroll', handleScroll, { passive: true });

    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, []);

  // Fixed header height prevents CLS - use consistent padding with visual changes only
  // Header always reserves the same space (80px on mobile, 96px on desktop)
  return (
    <header
      className={`fixed z-50 flex h-20 w-full items-center justify-between px-6 transition-colors duration-200 lg:h-24 lg:px-8 ${
        isRootPath && !isScrolled
          ? 'bg-transparent'
          : 'bg-white/20 backdrop-blur-md'
      }`}
    >
      <div className="mx-auto flex w-full max-w-2xl items-center lg:max-w-7xl">
        <Link
          href="/"
          className={`flex h-8 w-[160px] items-center lg:h-12 lg:w-[240px] ${
            isRootPath && !isScrolled ? 'text-white' : 'text-foreground'
          }`}
        >
          <ElevaCareLogo
            className="h-8 w-[160px] lg:h-12 lg:w-[240px]"
            variant={isRootPath && !isScrolled ? 'white' : 'default'}
          />
        </Link>
        <nav className="ml-auto flex items-center gap-4 sm:gap-6">
          <NavLink href="/#experts" isScrolled={isScrolled} isRootPath={isRootPath}>
            {t('findExpert')}
          </NavLink>
          <NavLink href="/about" isScrolled={isScrolled} isRootPath={isRootPath}>
            {t('mission')}
          </NavLink>
        </nav>
      </div>
    </header>
  );
}
